import java.nio.file.*;import java.util.stream.*;interface EqualwordsShort{static void main(String[]a)throws Exception{Files.lines(Paths.get(a[0])).collect(Collectors.groupingBy(w->Stream.of(w.split("")).sorted().collect(Collectors.joining()))).values().forEach(l->System.out.println(l.stream().collect(Collectors.joining(", "))));}}